import styled from "styled-components";

export const TypeWriterWrapper = styled.div`
	.typewrite > .wrap {
		border-right: 0.08em solid white;
		width: 100%;
		height: 100%;
		display: flex;
		flex-direction: column;
		justify-content: center;
	}
	justify-content: "center";
	position: absolute;

	position: absolute;
	top: 50%;
	left: 50%;
	margin-left: -150px;
	margin-top: -50px;
	color: #fff;
	font-family: "Courier New", Courier, monospace;
	font-size: 3em;

	@media screen and (max-width: ${props => props.theme.phone}) {
		top: 0 !important;
		left: 0 !important;
		margin-left: -150px !important;
		margin-top: 0px !important;
	}

	@media screen and (min-width: ${props =>
			props.theme.phone}) and (min-width: ${props => props.theme.tablet}) {
		height: 200px;
		top: 0 !important;
		left: 0 !important;
		margin-left: -150px !important;
		margin-top: 0px !important;
	}

	@media screen and (min-width: ${props => props.theme.tablet}) {
		top: 0 !important;
		left: 0 !important;
		margin-left: -150px !important;
		margin-top: 0px !important;
	}
`;
