import styled from "styled-components";

export const HoverTextWrapper = styled.div`
	p {
		transition: all 0.3s ease-out;
		/* font-family: "Squada One"; */
		font-family: "Kanit";
		margin: 0;
		color: white;
		&.h1 {
			font-size: 2em;
		}
		&.h2 {
			font-size: 4em;
		}
	}
	span {
		transition: width 2s, height 2s, transform 2s;
	}
	span:hover {
		color: #08fdd8;
		transform: rotate(180deg);
	}
`;
