import styled from "styled-components";

export const TypeWriterWrapper = styled.div`
	.typewrite > .wrap {
		border-right: 0.08em solid white;
		width: 100%;
		height: 100%;
		display: flex;
		flex-direction: column;
		justify-content: center;
	}
	position: absolute;
	color: #fff;
	font-family: "Courier New", Courier, monospace;
	font-size: 3em;

	@media screen and (max-width: 577px) {
		top: 0;
		left: 0;
		margin-left: 0;
		margin-top: 0px;
		font-size: 2em;
	}

	@media screen and (min-width: 577px) and (min-width: 801px) {
		height: 200px;
		top: 0;
		left: 0;
		margin-left: -150px;
		margin-top: 0px;
	}

	@media screen and (min-width: 577px) and (min-width: 801px) {
		height: 200px;
		top: 0;
		left: 0;
		margin-left: -150px;
		margin-top: 0px;
	}
	@media screen and (min-width: 801px) and (min-width: 993px) {
		top: 50%;
		left: 50%;
		margin-left: -120px;
		margin-top: -50px;
	}
	@media screen and (min-width: 801px) {
		top: 50%;
		left: 50%;
		margin-left: -150px;
		margin-top: -50px;
	}
`;
