import React, { useEffect, useState } from "react";
import styled, { ThemeProvider } from "styled-components";
import theme from "./Theme";
import GlobalStyles from "./Global";
// import Header from "./Header";
// import Sidebar from "./Sidebar";
import { ContainerFluid } from "./Container";

const LayoutWrapper = styled.div`
	padding: 0;
	margin: 0;
	display: flex;
	flex-direction: column;
	height: ${props => (props.noscroll ? "100vh" : "100%")};
	@media screen and (min-height: 1080px) {
		height: 100vh;
	}
	@media screen and (max-width: ${props => props.theme.tablet}) {
		height: 100% !important;
	}
	@media screen and (max-height: 660px) {
		height: 100%;
	}
`;

const Content = styled.div`
	flex: 1;
	display: flex;
	flex-direction: column;
	overflow: hidden;
	@media screen and (max-width: ${props => props.theme.tablet}) {
		overflow: visible;
	}
`;

const Layout = ({ children, bgURL, hideOverflow, noscroll, style }) => {
	return (
		<ThemeProvider theme={theme}>
			<React.Fragment>
				<GlobalStyles />
				<ContainerFluid>
					<LayoutWrapper bgURL={bgURL} noscroll={noscroll} style={style}>
						<Content hideOverflow={hideOverflow}>{children}</Content>
					</LayoutWrapper>
				</ContainerFluid>
			</React.Fragment>
		</ThemeProvider>
	);
};

export default Layout;
