import { useState } from "react";
import { Form } from "antd";
import { FormWrapper } from "./styles";
import styled from "styled-components";
import TextInput from "../input_fields/TextInput";
import LargeTextInput from "../input_fields/LargeTextInput";
const { Item } = Form;
const ContactFormContainer = styled.div`
	background: #ffffff;
	flex: 3;
	width: 100%;
	border-radius: 7px 0 0 7px;
	padding: 30px 40px;
	box-shadow: 0 22px 34px -10px rgba(38, 47, 67, 0.3);
	h2 {
		font-size: 20px;
		color: #0073af;
	}
	@media screen and (max-width: ${props => props.theme.phone}) {
		border-radius: 7px 7px 0 0;
		padding: 15px 40px;
	}

	@media screen and (min-width: ${props =>
			props.theme.phone}) and (min-width: ${props => props.theme.tablet}) {
		border-radius: 7px 0 0 7px;
		padding: 15px 40px;
	}

	@media screen and (min-width: ${props => props.theme.tablet}) {
		min-width: 583px;
	}
`;

const InputGroupWrapper = styled.div`
	display: flex;

	&:first-child {
		margin-top: 8px;
	}
	.custom-field {
		flex: 1;
		&.with-right-offset {
			margin-right: 24px;
			@media screen and (max-width: ${props => props.theme.tablet}) {
				margin-right: 0px;
			}
		}
	}

	.ant-checkbox-wrapper {
		display: block;
		padding-left: 15px;
		text-indent: -15px;
		line-height: 24px;
		font-size: 12px;
		a {
			color: ${props => props.theme.linkTextColor};
		}
		.ant-checkbox {
			width: 13px;
			height: 13px;
			padding: 0;
			margin: 0;
			vertical-align: bottom;
			position: relative;
			top: -7px;
		}
	}

	.ant-form-extra {
		text-align: right;
		position: absolute;
		width: 100%;
		top: 85px;
	}

	.custom-recaptcha {
		@media screen and (max-width: ${props => props.theme.phone}) {
			transform: scale(0.8) !important;
			transform-origin: 0 0 !important;
		}
	}

	@media screen and (max-width: ${props => props.theme.tablet}) {
		flex-direction: column;
	}
`;

const ContactForm = ({ form }) => {
	const {
		getFieldDecorator,
		setFieldsValue,
		getValueFromEvent,
		getFieldValue
	} = form;
	const [charLimit, setCharLimit] = useState(0);
	const handleSubmit = e => {
		e.preventDefault();

		form.validateFieldsAndScroll((err, values) => {});
	};
	return (
		<ContactFormContainer>
			<p className="title">Contact Me</p>
			<p>
				I am interested in freelance opportunities - especially ambitious or
				large projects. However, if you have other request or question, don't
				hesitate to contact me using below form either.
			</p>
			<Form className="form-group" onSubmit={handleSubmit}>
				<InputGroupWrapper>
					<Item className="custom-field">
						{getFieldDecorator("lastName", {
							rules: [
								{ required: true, message: "Please input your last name." },
								{
									pattern: /^[a-z ,.'-]+$/i,
									message: "Please input alphabetical letters only."
								},
								{ min: 2, message: "Please input at least 2 characters." }
							]
						})(
							<TextInput
								placeholder="Last Name"
								size="large"
								aria-label="last name"
							/>
						)}
					</Item>
					<Item className="custom-field">
						{getFieldDecorator("lastName", {
							rules: [
								{ required: true, message: "Please input your last name." },
								{
									pattern: /^[a-z ,.'-]+$/i,
									message: "Please input alphabetical letters only."
								},
								{ min: 2, message: "Please input at least 2 characters." }
							]
						})(
							<TextInput
								placeholder="Last Name"
								size="large"
								aria-label="last name"
							/>
						)}
					</Item>
				</InputGroupWrapper>

				<InputGroupWrapper>
					<Item className="custom-field with-right-offset">
						{getFieldDecorator("email", {
							rules: [
								{ required: true, message: "Please input your email address." },
								{
									type: "email",
									message: "Please input a valid email address."
								}
							]
						})(
							<TextInput placeholder="Email" size="large" aria-label="email" />
						)}
					</Item>
				</InputGroupWrapper>

				<InputGroupWrapper>
					<Item className="custom-field" extra={`${charLimit}/255`}>
						{getFieldDecorator("message", {
							rules: [{ required: true, message: "Please input your message." }]
						})(
							<LargeTextInput
								size="large"
								autosize={{ minRows: 3, maxRows: 3 }}
								placeholder="Message"
								maxLength="255"
								aria-label="message"
								onChange={e => {
									setCharLimit(e.target.value.length);
								}}
							/>
						)}
					</Item>
				</InputGroupWrapper>
				<InputGroupWrapper>
					<Item className="custom-field with-right-offset">
						{getFieldDecorator("termAgreed", {
							rules: [
								{
									required: true,

									message: "You must agree to the Terms and Conditions."
								}
							]
						})(
							<Checkbox>
								I Agree to the
								<Link href="/terms-and-conditions" prefetch>
									<a> Terms and Conditions </a>
								</Link>
								and
								<Link href="/privacy-policy" prefetch>
									<a> Privacy Policies </a>
								</Link>
								of Alveo Land Corp.
							</Checkbox>
						)}
					</Item>
				</InputGroupWrapper>

				<InputGroupWrapper>
					<Item className="custom-field with-right-offset">
						{getFieldDecorator("recaptchaToken", {
							rules: [
								{
									required: true,
									message: "Please verify that you are not a robot."
								}
							]
						})(
							<ReCAPTCHA
								className="custom-recaptcha"
								sitekey={credentials.GOOGLE_RECAPTCHA_SITE_KEY}
								ref={e => (captcha = e)}
							/>
						)}
					</Item>
					<Item>
						<SkewedButton
							htmlType="submit"
							disabled={isFormSubmitting || messageSent}
							className={messageSent ? "success" : ""}
						>
							<span className="submit-icon">{renderIcon()}</span>
							<span className="submit-message">{renderButtonMessage()}</span>
						</SkewedButton>
					</Item>
				</InputGroupWrapper>
			</Form>
		</ContactFormContainer>
	);
};
export default Form.create()(ContactForm);
