import styled from "styled-components";

export const HoverTextWrapper = styled.div`
	p {
		margin: 0;
		color: white;
		&.h1 {
			font-size: 2em;
		}
		&.h2 {
			font-size: 4em;
		}
	}
	span:hover {
		color: blue;
	}
`;
