import styled from "styled-components";

export const ContactWrapper = styled.div`
	display: flex;
	flex-direction: row;
	background: #1a1a1a;
	height: 100vh;
	> div {
		padding: 30px;
		width: 100%;
	}
	@media screen and (max-width: ${props => props.theme.tablet}) {
		flex-wrap: wrap;
		div {
			padding: 20px;
		}
	}
	@media screen and (min-width: ${props => props.theme.tablet}) {
	}
`;

export const FormWrapper = styled.div`
	/* background: white;
	box-shadow: 2px 2px 4px 0 rgba(0, 0, 0, 0.5);
  border-radius: 3px; */

	p {
		font-family: "Open Sans", sans-serif;
		color: white;
		width: 80%;
	}
	.title {
		font-size: 2em;
		font-weight: bold;
	}
	@media screen and (max-width: ${props => props.theme.tablet}) {
		p {
			width: 100%;
		}
	}
	@media screen and (min-width: ${props => props.theme.tablet}) {
	}
`;
export const InputGroupWrapper = styled.div`
	display: flex;

	&:first-child {
		margin-top: 8px;
	}
	.custom-field {
		flex: 1;
		&.with-right-offset {
			margin-right: 24px;
			@media screen and (max-width: ${props => props.theme.tablet}) {
				margin-right: 0px;
			}
		}
	}

	.ant-checkbox-wrapper {
		display: block;
		padding-left: 15px;
		text-indent: -15px;
		line-height: 24px;
		font-size: 12px;
		a {
			color: ${props => props.theme.linkTextColor};
		}
		.ant-checkbox {
			width: 13px;
			height: 13px;
			padding: 0;
			margin: 0;
			vertical-align: bottom;
			position: relative;
			top: -7px;
		}
	}

	.ant-form-extra {
		text-align: right;
		position: absolute;
		width: 100%;
		top: 85px;
	}

	.custom-recaptcha {
		@media screen and (max-width: ${props => props.theme.phone}) {
			transform: scale(0.8) !important;
			transform-origin: 0 0 !important;
		}
	}

	@media screen and (max-width: ${props => props.theme.tablet}) {
		flex-direction: column;
	}
`;
