import { Input } from "antd";
import React from "react";
import styled from "styled-components";

const { TextArea } = Input;

const LargeTextInputWrapper = styled.div`
  position: relative;
  display: inline-block;
  width: 100%;
  .custom-placeholder {
    padding: 0 8px 0 11px;
    pointer-events: none;
    position: absolute;
    left: 0;
    top: 0;
    transition: 0.2s;
    transition-timing-function: ease;
    transition-timing-function: cubic-bezier(0.25, 0.1, 0.25, 1);
    opacity: 0.5;
  }

  textarea {
    padding: 10px 10px 10px 14px;
    &:focus + .custom-placeholder,
    &:not(:placeholder-shown) + .custom-placeholder {
      display: flex;
      justify-content: center;
      align-items: center;
      transform: scale(0.85) translateY(-11px);
      opacity: 1;
      background: #fff;
      color: #0073af;
      height: 20px;
    }

    &:not(:focus) + .custom-placeholder {
      color: #a8a8a8;
    }

    /* For IE Browsers*/

    &:focus + .custom-placeholder,
    &:not(:ms-input-placeholder) + .custom-placeholder {
      display: flex;
      justify-content: center;
      align-items: center;
      transform: scale(0.85) translateY(-11px);
      opacity: 1;
      background: #fff;
      color: #0073af;
      height: 20px;
    }
  }
`;

const LargeTextInput = React.forwardRef((props, ref) => {
  const { placeholder, className } = props;
  return (
    <LargeTextInputWrapper ref={ref} className={className}>
      <TextArea {...props} placeholder=" " />
      <span className="custom-placeholder">{placeholder}</span>
    </LargeTextInputWrapper>
  );
});

export default LargeTextInput;
